package client

import (
	"net/http"
	"net/http/httptest"
	"testing"

	"github.com/stretchr/testify/assert"

	"github.com/tab/smart-id/internal/config"
	"github.com/tab/smart-id/internal/models"
)

func Test_Session_Status(t *testing.T) {
	id := "8fdb516d-1a82-43ba-b82d-be63df569b86"

	tests := []struct {
		name     string
		before   func(w http.ResponseWriter, r *http.Request)
		id       string
		expected *models.SessionResponse
		error    bool
	}{
		{
			name: "Success",
			before: func(w http.ResponseWriter, r *http.Request) {
				w.Header().Set("Content-Type", "application/json")
				w.Write([]byte(`
{
	"state": "COMPLETE",
	"result": {
		"endResult": "OK",
		"documentNumber": "PNOEE-30303039914"
	},
	"signature": {
		"value": "",
		"algorithm": "sha256WithRSAEncryption"
	},
	"cert": {
		"value": "",
		"certificateLevel": "QUALIFIED"
	},
	"interactionFlowUsed": "displayTextAndPIN"
}`))
			},
			id: id,
			expected: &models.SessionResponse{
				State: "COMPLETE",
				Result: models.Result{
					EndResult:      "OK",
					DocumentNumber: "PNOEE-30303039914",
				},
				Signature: models.Signature{
					Value:     "",
					Algorithm: "sha256WithRSAEncryption",
				},
				Cert: models.Certificate{
					Value:            "",
					CertificateLevel: "QUALIFIED",
				},
				InteractionFlowUsed: "displayTextAndPIN",
			},
			error: false,
		},
		{
			name: "Error: USER_REFUSED",
			before: func(w http.ResponseWriter, r *http.Request) {
				w.Header().Set("Content-Type", "application/json")
				w.Write([]byte(`{"state": "COMPLETE", "result": {"endResult": "USER_REFUSED"}}`))
			},
			id: id,
			expected: &models.SessionResponse{
				State: "COMPLETE",
				Result: models.Result{
					EndResult: "USER_REFUSED",
				},
			},
			error: false,
		},
		{
			name: "Error: TIMEOUT",
			before: func(w http.ResponseWriter, r *http.Request) {
				w.Header().Set("Content-Type", "application/json")
				w.Write([]byte(`{"state": "COMPLETE", "result": {"endResult": "TIMEOUT"}}`))
			},
			id: id,
			expected: &models.SessionResponse{
				State: "COMPLETE",
				Result: models.Result{
					EndResult: "TIMEOUT",
				},
			},
			error: false,
		},
		{
			name: "Not found",
			before: func(w http.ResponseWriter, r *http.Request) {
				w.Header().Set("Content-Type", "application/json")
				w.WriteHeader(http.StatusNotFound)
				w.Write([]byte(`{"title": "Not Found", "status": 404}`))
			},
			id:       id,
			expected: &models.SessionResponse{},
			error:    true,
		},
		{
			name: "Bad Request",
			before: func(w http.ResponseWriter, r *http.Request) {
				w.Header().Set("Content-Type", "application/json")
				w.WriteHeader(http.StatusBadRequest)
				w.Write([]byte(`{"title": "Bad Request", "status": 400}`))
			},
			id:       id,
			expected: &models.SessionResponse{},
			error:    true,
		},
	}

	for _, tt := range tests {
		t.Run(tt.name, func(t *testing.T) {
			testServer := httptest.NewServer(http.HandlerFunc(tt.before))
			defer testServer.Close()

			client, err := NewClient(
				config.WithRelyingPartyName("DEMO"),
				config.WithRelyingPartyUUID("00000000-0000-0000-0000-000000000000"),
				config.WithURL(testServer.URL),
			)
			assert.NoError(t, err)

			response, err := client.Status(tt.id)

			if tt.error {
				assert.Error(t, err)
			} else {
				assert.NoError(t, err)
				assert.Equal(t, tt.expected.State, response.State)
				assert.Equal(t, tt.expected.Result, response.Result)
			}
		})
	}
}
